import React, { Component } from "react";
import { Button, Input } from "antd";
import _ from "lodash";
import { NONAME } from "dns";

class Edit extends Component {
  state = {
    val: this.props.value,
    input: "block"
  };

  handleChange = e => {
    const todos = this.props.todos;
    todos[this.props.index] = e;
    console.log(todos);
    if (_.uniq(todos).length !== todos.length) {
      alert("value Exists,Please enter different todo");
    } else {
      this.props.addtodobyindex(todos);

      console.log(todos);
    }
    this.setState({
      inputValue: "",
      input: "none"
    });
  };
  render() {
    console.log(this.props.index, this.props.value);
    return (
      <div className={this.state.input}>
        <Input
          placeholder="Basic usage"
          size="middle"
          style={{ width: "300px" }}
          value={this.state.val}
          onChange={e =>
            this.setState({
              val: e.target.value
            })
          }
        />

        <Button type="primary" onClick={e => this.handleChange(this.state.val)}>
          add
        </Button>
      </div>
    );
  }
}

export default Edit;
