import React, { Component } from "react";
import { Button, Input } from "antd";

class Edit extends Component {
  state = {
    val: this.props.value
  };

  handleChange = e => {
    const todos = this.props.todos;
    if (todos.indexOf(this.state.val) !== -1) {
      alert("value Exists,Please enter different todo");
    } else {
      this.props.removetodo(this.props.index, this.props.todos);
      this.props.addtodobyindex(e, this.props.index);
      this.props.todofn(e);
    }
    this.setState({
      inputValue: ""
    });
  };
  render() {
    console.log(this.props.index, this.props.value);
    return (
      <div>
        <Input
          placeholder="Basic usage"
          size="middle"
          style={{ width: "300px" }}
          value={this.state.val}
          onChange={e =>
            this.setState({
              val: e.target.value
            })
          }
        />

        <Button type="primary" onClick={e => this.handleChange(this.state.val)}>
          add
        </Button>
      </div>
    );
  }
}

export default Edit;
