import React, { Component } from "react";
import { Button } from "antd";
import EditForm from "./edit";

class Todos extends Component {
  deleteTodo = e => {
    console.log(e);
    this.props.removeTodo(e, this.props.allTodos);
  };

  EditTodo = (e, ind) => {
    console.log(e, ind);
  };

  render() {
    return (
      <div
        style={{
          fontWeight: "bold",
          padding: "40px",
          border: "red",
          fontSize: "30px"
        }}
      >
        <ol>
          {this.props.allTodos.map((value, index) => {
            return (
              <div
                key={index}
                style={{ display: "flex", alignItems: "center" }}
              >
                <li>{value}</li>{" "}
                <Button
                  type="danger"
                  onClick={e => {
                    this.deleteTodo(index);
                  }}
                  style={{ margin: "0 10px" }}
                >
                  delete
                </Button>
                <Button
                  type="primary"
                  onClick={(e, a) => this.EditTodo(value, index)}
                  style={{ margin: "0 10px" }}
                >
                  edit
                </Button>
                <EditForm myfun={this.EditTodo} />
              </div>
            );
          })}
        </ol>
      </div>
    );
  }
}

export default Todos;
