import { ADD_TODO, REMOVE_TODO } from "../actions/formAction";

const initialState = {
  id: "",
  todos: []
};

export const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_TODO:
      return {
        ...state,
        todos: [...state.todos, action.value]
      };
    case REMOVE_TODO:
      return {
        ...state,
        id: action.payload,
        todos: ![...state.todos].splice(action.payload, 1)
      };
    default:
      return state;
  }
};

export default reducer;
