import React, { Component } from "react";
import { Button } from "antd";
import EditForm from "./edit";

class Todos extends Component {
  state = {
    index: "",
    value: ""
  };

  deleteTodo = e => {
    console.log(e);
    this.props.removeTodo(e, this.props.allTodos);
  };

  EditTodo = (ind, val) => {
    this.setState({
      index: ind,
      value: val
    });
  };

  render() {
    console.log(this.props.allTodos);
    return (
      <div
        style={{
          fontWeight: "bold",
          padding: "40px",
          border: "red",
          fontSize: "30px"
        }}
      >
        <ol>
          {this.props.allTodos.map((value, index) => {
            return (
              <div
                key={index}
                style={{ display: "flex", alignItems: "center" }}
              >
                <li>{value}</li>{" "}
                <Button
                  type="danger"
                  onClick={e => {
                    this.deleteTodo(index);
                  }}
                  style={{ margin: "0 10px" }}
                >
                  delete
                </Button>
                <Button
                  type="primary"
                  onClick={(e, a) => {
                    this.EditTodo(index, value);
                  }}
                  style={{ margin: "0 10px" }}
                >
                  edit
                </Button>
                {index === this.state.value ? (
                  <EditForm index={this.state.index} value={this.state.value} />
                ) : null}
              </div>
            );
          })}
        </ol>
      </div>
    );
  }
}

export default Todos;
